import React, { useState, useEffect } from "react";
import Grid from "./Grid";

function App() {
  const [grid, setGrid] = useState([]);

  useEffect(() => {
    // initialize empty grid 20×20
    const rows = 20, cols = 20;
    const newGrid = Array.from({ length: rows }, (_, r) =>
      Array.from({ length: cols }, (_, c) => ({ row: r, col: c, isWall: false }))
    );
    setGrid(newGrid);
    console.log("Grid initialized", newGrid);
  }, []);

  return (
    <div className="App">
      {grid.length > 0 ? (
        <Grid grid={grid} />
      ) : (
        <p>Initializing grid…</p>
      )}
    </div>
  );
}

export default App;
